import "./style.css";

import { Helmet, HelmetProvider } from "react-helmet-async";
import { introdata, meta } from "../../content_option";

import { Portfolio } from "../portfolio";
import { Projects } from "../projects";
import React, { useState, useEffect } from "react";
import lightProfile from '../../assets/images/profile2.png';
import darkProfile from '../../assets/images/darkprofile.png';

export const Home = () => {
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "light");
  const [currentVideo, setCurrentVideo] = useState(0);

  useEffect(() => {
    const observer = new MutationObserver(() => {
      const currentTheme = document.documentElement.getAttribute('data-theme');
      if (currentTheme) {
        setTheme(currentTheme);
      }
    });

    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ['data-theme']
    });

    return () => observer.disconnect();
  }, []);

  const profileImage = theme === "dark" ? darkProfile : lightProfile;

  // Software videos data - you can add your video URLs here
  const softwareVideos = [
    {
      title: "AltCanvas",
      description: "AI-powered image editor for accessibility",
      videoUrl: "https://www.youtube.com/embed/tJUqjjwSxPs",
      link: "https://shljessie.github.io/AltCanvas/"
    },
    {
      title: "TeachYou",
      description: "LLM-based teachable agent for programming",
      videoUrl: "https://www.youtube.com/embed/MEtcA6GjfAg",
      link: "https://jhw123.github.io/project/teachyou"
    },
    {
      title: "DarakBooks",
      description: "LLM-based book recommendation system",
      videoUrl: "", // Add your video URL
      link: "https://darakbooks.vercel.app/onboarding"
    }
  ];

  const nextVideo = () => {
    setCurrentVideo((prev) => (prev + 1) % softwareVideos.length);
  };

  const prevVideo = () => {
    setCurrentVideo((prev) => (prev - 1 + softwareVideos.length) % softwareVideos.length);
  };

  return (
    <HelmetProvider>
      <section id="home" className="home">
        <Helmet>
          <meta charSet="utf-8" />
          <title> {meta.title}</title>
          <meta name="description" content={meta.description} />
        </Helmet>
        <div className="intro_sec d-block d-lg-flex align-items-center ">
          <div className="text order-2 order-lg-1 h-100 d-lg-flex justify-content-center">
            <div className="align-self-center ">
            <br/>                <br/>
              <div className="intro mx-auto">
                <div className="intro-content">
                  <div className="intro-left">
                    <img width="500px" alt="" src={profileImage} />
                  </div>
                  <div className="intro-right">
                    <h2 className="mb-1x">{introdata.title}</h2>
                    <p className="intro-email"><a href="mailto:shlee@cs.stanford.edu">shlee@cs.stanford.edu</a></p>

                    <p className="intro-bio">
                    AI Platform Software Engineer @ <b>NVIDIA</b>.<br></br><br></br>
                    Previously, I completed my Master's in Computer Science at <b>Stanford University</b>, where I researched Human-AI Interaction and worked at <b>Microsoft Research</b> on multimodal agent systems.</p>
                  </div>
                </div>
              </div>
            </div>
          </div>


          {/* Google Experiments inspired geometric shapes */}
          <div className="geometric-shape"></div>
        </div>

        {/* Pieces of Software Section */}
        <div className="software-section">
          <div className="container">
            <h2 className="software-title">Pieces of Software</h2>
            <div className="software-carousel">
              <div className="carousel-track" style={{ transform: `translateX(-${currentVideo * 100}%)` }}>
                {softwareVideos.map((video, index) => (
                  <div key={index} className="carousel-slide">
                    <div className="video-card">
                      <div className="video-container">
                        {video.videoUrl ? (
                          <iframe
                            src={video.videoUrl}
                            title={video.title}
                            frameBorder="0"
                            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                            allowFullScreen
                          ></iframe>
                        ) : (
                          <div className="video-placeholder">
                            <p>Video coming soon</p>
                          </div>
                        )}
                      </div>
                      <div className="video-info">
                        <h3>{video.title}</h3>
                        <p>{video.description}</p>
                        {video.link && (
                          <a href={video.link} target="_blank" rel="noreferrer" className="software-link">
                            View Project →
                          </a>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="slider-progress">
              <div className="progress-bar">
                <div 
                  className="progress-fill" 
                  style={{ width: `${((currentVideo + 1) / softwareVideos.length) * 100}%` }}
                ></div>
              </div>
              <div className="slider-controls">
                <button className="control-btn" onClick={prevVideo} disabled={currentVideo === 0}>
                  ← Previous
                </button>
                <span className="slide-counter">
                  {currentVideo + 1} / {softwareVideos.length}
                </span>
                <button className="control-btn" onClick={nextVideo} disabled={currentVideo === softwareVideos.length - 1}>
                  Next →
                </button>
              </div>
            </div>
          </div>
        </div>
        <p class="down"> ↓ Research & Projects  ↓</p>
      </section>
      <Portfolio/>
      <Projects/>
    </HelmetProvider>
  );

};
